import React from 'react';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>SipScore</h1>
        <h3>The ultimate reward system for your bar</h3>
        <button>Mint</button>
        <button>Connect</button>
        <div>
          <label htmlFor="nft-id">NFT ID:</label>
          <input type="text" id="nft-id" name="nft-id" />
          <label htmlFor="points-earned">Points Earned:</label>
          <input type="text" id="points-earned" name="points-earned" />
        </div>
        <div>
          <p>Your balance is: <span id="balance">0</span></p>
          <button>Redeem reward</button>
        </div>
      </header>
    </div>
  );
}

export default App;
